import "./App.css";

import React, { useState } from "react";
import {marked} from "marked";
import useLocalStorage from "./components/useLocalStorage";

const App = () => {
  const [code, setCode] = useLocalStorage("markdown", "## Hello");
  const [compiled, setCompiled] = useState(marked(code));
  const [hide, setHide] = useState(true);
  const [docs, setDocs] = useState(null);
  const [showDocs, setShowDocs] = useState(false);

  const openMD = () => {
    setHide(true);
    setShowDocs(false);
  };

  const openPreview = () => {
    setHide(false);
    setShowDocs(false);
  };

  const handleChange = (e) => {
    const newCode = e.target.value;
    setCode(newCode);
    setCompiled(marked(newCode));
  };

  const fetchDocs = async () => {
    try {
      const response = await fetch(
        "https://cors-anywhere.herokuapp.com/https://www.markdownguide.org/api/v1/basic-syntax.json"
      );
      const responseData = await response.json();
      setDocs(responseData.basic_syntax);
      setShowDocs(true);
      setHide(false);
    } catch (error) {
      console.error("Error fetching docs:", error);
    }
  };

  return (
    <>
      <h1>MarkDown Previewer React App</h1>

      <div className="container">
        <div className="btns">
          <button onClick={openMD} className="btn">
            MarkDown
          </button>
          <button onClick={fetchDocs}>Docs</button>
          <button onClick={openPreview}>Preview</button>
        </div>

        {hide ? (
          <div>
            <textarea onChange={handleChange} value={code} />
          </div>
        ) : null}

        {showDocs && docs ? (
          <p>
             <div className="docs-container">
            <h2>Docs</h2>
            <ul className="docs-list">
              {docs.map((doc, index) => (
                <li key={index} className="doc-item">
                  <h3>{doc.name}</h3>
                  <p>{doc.description}</p>
                  <pre>{doc.code}</pre>
                </li>
              ))}
            </ul>
          </div>
            {/* {JSON.stringify(docs, null, 50)} */}
          </p>
        ) : null}

        {!hide && !showDocs ? (
          <div>
            <textarea value={compiled} readOnly />
          </div>
        ) : null}
      </div>
    </>
  );
};

export default App;
