import "./App.css";
import React, { useState } from "react";
import { marked } from "marked";
import useLocalStorage from "./components/useLocalStorage";


const App = () => {
  const [code, setCode] = useLocalStorage("markdown", "## Hello");
  const [compiled, setCompiled] = useState(marked(code));
  const [hide, hidePreview] = useState(true);
  const [docs, setDocs] = useState(null);

  const openMD = () => {
    console.log(0);
    hidePreview(true);
  };

  const openPreview = () => {
    console.log(0);
    hidePreview(false);
  };

  const handleChange = (e) => {
    const newCode = e.target.value;
    setCode(newCode);
    setCompiled(marked(newCode));
  };
  const fetchDocs = async () => {
    try {
      const response = await fetch(
        "https://cors-anywhere.herokuapp.com/https://www.markdownguide.org/api/v1/basic-syntax.json"
      );
      console.log(response);
      const responseData = await response.json();
      console.log(responseData.basic_syntax);
      setDocs(responseData.basic_syntax);
    } catch (error) {
      console.error("Error fetching docs:", error);
    }
  };
  
  const [showDocs, setShowDocs] = useState(false);

  const toggleDocs = () => {
    setShowDocs(!showDocs);
  };
  return (
    <>
      <h1>MarkDown Previewer React App</h1>

      <div className="container">
        <div className="btns">
          <button onClick={openMD} className="btn">
            MarkDown
          </button>
          <button onClick={()=>{fetchDocs();toggleDocs()}}>Docs</button>
          <button onClick={openPreview}>Preview</button>
        </div>

        {hide ? (
          <div>
            <textarea onChange={handleChange} value={code} />
          </div>
        ) : (
          <div>
            <textarea value={compiled} readOnly />
          </div>
        )}
        
        {showDocs && docs ? (
          <div>
            <h2>Docs</h2>
            {/* Render the documentation here */}
            <ul>
              {docs.map((doc, index) => (
                <li key={index}>{doc.title}</li>
              ))}
            </ul>
          </div>
        ) : null}
      </div>
    </>
  );
};



export default App;
